import "./App.css";
import React from "react";
import { UilArrowUp } from "@iconscout/react-unicons";
import { UilArrowDown } from "@iconscout/react-unicons";
import { UilPlay } from "@iconscout/react-unicons";
import { UilPause } from "@iconscout/react-unicons";
import { UilRepeat } from "@iconscout/react-unicons";
// import accurateInterval from "accurate-interval";

var timeInterval = function (callback, delay) {
  var timerId,
    start,
    remaining = delay;

  this.pause = function () {
    window.clearTimeout(timerId);
    timerId = null;
    remaining -= Date.now() - start;
  };

  this.resume = function () {
    if (timerId) {
      return;
    }

    start = Date.now();
    timerId = window.setTimeout(callback, remaining);
  };

  this.resume();
};

class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breakLength: 5,
      sessLength: 25,
      timer: 1500,
      countdown: "inactive",
      interval: "",
    };
    this.increaseBreak = this.increaseBreak.bind(this);
    this.decreaseBreak = this.decreaseBreak.bind(this);
    this.increaseSession = this.increaseSession.bind(this);
    this.decreaseSession = this.decreaseSession.bind(this);
    this.timeConverter = this.timeConverter.bind(this);
    this.reset = this.reset.bind(this);
    this.startTimer = this.startTimer.bind(this);
    this.startBreak = this.startBreak.bind(this);
    this.startCountdown = this.startCountdown.bind(this);
    this.decreaseTimer = this.decreaseTimer.bind(this);
  }

  increaseBreak() {
    let value = this.state.breakLength;
    if (value === 60) {
      return value;
    }
    this.setState({
      breakLength: value + 1,
    });
  }

  decreaseBreak() {
    let value = this.state.breakLength;
    if (value === 1) {
      return value;
    }
    this.setState({
      breakLength: value - 1,
    });
  }

  increaseSession() {
    let value = this.state.sessLength;
    if (value === 60) {
      return value;
    }
    this.setState({
      sessLength: value + 1,
      timer: this.state.timer + 60,
    });
  }

  decreaseSession() {
    let value = this.state.sessLength;
    if (value === 1) {
      return value;
    }
    this.setState({
      sessLength: value - 1,
      timer: this.state.timer - 60,
    });
  }

  reset() {
    this.setState({
      breakLength: 5,
      sessLength: 25,
      timer: 1500,
      countdown: "inactive",
      intervalID: "",
    });
  }

  timeConverter() {
    let minutes = Math.floor(this.state.timer / 60);
    let seconds = this.state.timer - minutes * 60;
    seconds = seconds < 10 ? "0" + seconds : seconds;
    minutes = minutes < 10 ? "0" + minutes : minutes;
    return minutes + ":" + seconds;
  }

  startTimer() {
    if (this.state.countdown == "inactive") {
      this.setState({
        countdown: "active",
      });
      this.startCountdown();
    } else {
      this.setState({
        countdown: "inactive",
      });
      this.state.interval.pause();
    }
  }

  decreaseTimer() {
    this.setState({
      timer: this.state.timer - 1,
    });
  }

  startCountdown() {
    var time = new timeInterval(this.decreaseTimer(), 1000);

    if (this.state.countdown == "active") {
      if (this.state.timer == 0) {
        this.startBreak();
      } else {
        this.setState({
          interval: new timeInterval(this.decreaseTimer(), 1000),
        });
        // Get timer to stop when pause button is clicked.
      }
    }
  }

  startBreak() {
    if (this.state.breakLength === 0) {
      this.reset();
    }
  }

  render() {
    return (
      <div>
        <h1>Pomodoro Technique</h1>
        <SessionControls
          increaseBreak={this.increaseBreak}
          decreaseBreak={this.decreaseBreak}
          breakLength={this.state.breakLength}
          increaseSession={this.increaseSession}
          decreaseSession={this.decreaseSession}
          sessLength={this.state.sessLength}
        />
        <div className="timer">
          <div id="timer-label" class="label">
            <h2>Session</h2>
          </div>
          <div className="numbers">{this.timeConverter()}</div>
          <TimerControls reset={this.reset} startTimer={this.startTimer} />
        </div>
      </div>
    );
  }
}

class SessionControls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="top-controls">
        <div className="break controls">
          <div id="break-label" className="label">
            <h2> Break Length </h2>
          </div>
          <div className="scontrols">
            <button
              onClick={this.props.increaseBreak}
              className="session-btns"
              id="break-increment"
            >
              <span className="arrows">
                <UilArrowUp />
              </span>
            </button>
            <p className="numbers">{this.props.breakLength}</p>
            <button
              onClick={this.props.decreaseBreak}
              className="session-btns"
              id="break-decrement"
            >
              <span className="arrows">
                <UilArrowDown />
              </span>
            </button>
          </div>
        </div>
        <div className="session controls">
          <div id="session-label" className="label">
            <h2> Session Length</h2>
          </div>
          <div className="scontrols">
            <button
              onClick={this.props.increaseSession}
              className="session-btns"
              id="session-increment"
            >
              <span className="arrows">
                <UilArrowUp />
              </span>
            </button>
            <p className="numbers">{this.props.sessLength}</p>
            <button
              onClick={this.props.decreaseSession}
              className="session-controls"
              id="session-decrement"
            >
              <span className="arrows">
                <UilArrowDown />
              </span>
            </button>
          </div>
        </div>
      </div>
    );
  }
}

class TimerControls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="timer-controls">
        <div id="start_stop">
          <div className="timer-btns">
            <button onClick={this.props.startTimer}>
              <UilPlay />
              <UilPause />
            </button>
          </div>
        </div>
        <div id="reset">
          <button onClick={this.props.reset}>
            <UilRepeat />
          </button>
        </div>
      </div>
    );
  }
}
export default Timer;
