import "./App.css";
import React from "react";

class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breakLength: 5,
      sessLength: 25,
    };
  }

  render() {
    return (
      <div>
        <h1>Pomodoro Technique </h1>
        <div className="session-controls">
          <div id="break-label">{this.state.breakLength}</div>
          <div id="session-label">{this.state.sessLength}</div>
        </div>
        <div className="timer"></div>
        <div className="timer-controls"></div>
      </div>
    );
  }
}

export default Timer;
