import "./App.css";
import React from "react";
import { UilArrowUp } from "@iconscout/react-unicons";
import { UilArrowDown } from "@iconscout/react-unicons";

class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breakLength: 5,
      sessLength: 25,
    };
    this.increaseTimer = this.increaseTimer.bind(this);
    this.decreaseTimer = this.decreaseTimer.bind(this);
  }

  increaseTimer() {
    this.setState((state) => ({
      sessLength: state.sessLength + 1,
    }));
  }

  decreaseTimer() {
    this.setState((state) => ({
      sessLength: state.sessLength - 1,
    }));
  }

  render() {
    return (
      <div>
        <h1>Pomodoro Technique </h1>
        <SessionControls
          increaseTimer={this.increaseTimer}
          breakLength={this.state.breakLength}
        />
      </div>
    );
  }
}

class SessionControls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="timer-controls">
        <div id="break=label">
          <button
            onclick={this.props.increaseTimer}
            className="session-controls"
            id="break-increment"
          >
            {UilArrowUp}
          </button>
          <p>{this.props.breakLength}</p>
          <button
            onclick={this.props.increaseTimer}
            className="session-controls"
            id="break-decrement"
          >
            {UilArrowDown}
          </button>
        </div>
      </div>
    );
  }
}
export default Timer;
