import "./App.css";
import React from "react";
import { UilArrowUp } from "@iconscout/react-unicons";
import { UilArrowDown } from "@iconscout/react-unicons";

class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      breakLength: 5,
      sessLength: 25,
    };
    this.increaseBreak = this.increaseBreak.bind(this);
    this.decreaseBreak = this.decreaseBreak.bind(this);
    this.increaseTimer = this.increaseTimer.bind(this);
    this.decreaseTimer = this.decreaseTimer.bind(this);
  }

  increaseBreak() {
    let value = this.state.breakLength;
    this.setState({
      breakLength: value + 1,
    });
  }

  decreaseBreak() {
    let value = this.state.breakLength;
    this.setState({
      breakLength: value - 1,
    });
  }

  increaseTimer() {
    let value = this.state.sessLength;
    this.setState({
      sessLength: value + 1,
    });
  }

  decreaseTimer() {
    let value = this.state.sessLength;
    this.setState({
      sessLength: value - 1,
    });
  }
  render() {
    return (
      <div>
        <h1>Pomodoro Technique</h1>
        <SessionControls
          increaseBreak={this.increaseBreak}
          decreaseBreak={this.decreaseBreak}
          breakLength={this.state.breakLength}
          increaseTimer={this.increaseTimer}
          decreaseTimer={this.decreaseTimer}
          sessLength={this.state.sessLength}
        />
      </div>
    );
  }
}

class SessionControls extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className="timer-controls">
        <div id="break-label" className="timer-labels">
          <button
            onclick={this.props.increaseBreak}
            className="session-controls"
            id="break-increment"
          >
            <span className="arrows">
              <UilArrowUp />
            </span>
          </button>
          <p>{this.props.breakLength}</p>
          <button
            onclick={this.props.decreaseBreak}
            className="session-controls"
            id="break-decrement"
          >
            <span className="arrows">
              <UilArrowDown />
            </span>
          </button>
        </div>
        <div id="session-label" className="timer-labels">
          <button
            onclick={this.props.increaseTimer}
            className="session-controls"
            id="session-increment"
          >
            <span className="arrows">
              <UilArrowUp />
            </span>
          </button>
          <p>{this.props.sessLength}</p>
          <button
            onclick={this.props.decreaseTimer}
            className="session-controls"
            id="session-decrement"
          >
            <span className="arrows">
              <UilArrowDown />
            </span>
          </button>
        </div>
      </div>
    );
  }
}
export default Timer;
